  const [name, setName] = useState('')
  const [list, setList] = useState([])
  const [isEditing, setIsEditing] = useEffect(false)
  const [editId, setEditId] = useEffect(null)
  const [alert, setAlert] = useEffect({ show: false, msg: '', type: '' })

  return <section className="section-center">
    <h2>Grocery Bud</h2>
    <form className="form">
      <input type="text" className=""></input>
      <button type="submit" className="btn"></button>
    </form>
    <div className="grocery-container">
      <List />
    </div>
  </section>
}

export default App
